import React from "react";
import Card1 from "../main/components/Card1";
import Card2 from "../main/components/Card2";
import Card3 from "../main/components/Card3";

const Hero = () => {
  return (
    <div
      className="bg-cover bg-no-repeat bg-center h-[675px] lg:h-[550px] xl:h-[500px] 2xl:h-[400px] relative"
      style={{ backgroundImage: `url('images/Vector.png')` }}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex flex-col items-center justify-center h-full text-center">
        <div className="mt-20 sm:mt-32 lg:mt-40 xl:mt-44 2xl:mt-52">
          <span className="relative block mb-4">
            <img
              src="images/icon 2.png"
              alt=" logo"
              className="w-16 sm:w-20 lg:w-24"
            />
          </span>
          <h1 className="text-white text-2xl sm:text-3xl lg:text-4xl xl:text-5xl 2xl:text-6xl font-bold mb-2 sm:mb-3 lg:mb-4 xl:mb-5">
            Access Innovative Healthcare
          </h1>
          <p className="text-white text-sm sm:text-base lg:text-lg xl:text-xl 2xl:text-2xl mb-4 sm:mb-6 lg:mb-8">
            Empowering primary care professionals to seamlessly identify and
            engage with eligible patients for clinical trial opportunities.
          </p>
          <button className="bg-white text-[#392B85] py-2 px-6 sm:py-3 sm:px-8 lg:py-4 lg:px-10 rounded-full hover:bg-[#392B85] hover:text-white transition duration-300">
            Contact Us
          </button>
        </div>
      </div>
      <div className="container mx-auto mt-8 sm:mt-12 lg:mt-16 px-4 sm:px-6 lg:px-8 flex flex-wrap justify-center gap-4 sm:gap-6 xl:gap-8">
        <Card1 className="w-60 sm:w-72 lg:w-80 xl:w-96" />
        <Card2 className="w-60 sm:w-72 lg:w-80 xl:w-96" />
        <Card3 className="w-60 sm:w-72 lg:w-80 xl:w-96" />
      </div>
    </div>
  );
};

export default Hero;
