import React from "react";
import Card1 from "../main/components/Card1";
import Card2 from "../main/components/Card2";
import Card3 from "../main/components/Card3";

const Hero = () => {
  const backgroundStyle = {
    backgroundImage: `url('images/Vector.png')`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
  };

  return (
    <div
      style={backgroundStyle}
      className="min-h-[675px] bg-cover bg-no-repeat"
      id="hero"
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex justify-center items-center h-full">
        <div className="flex flex-col items-center text-center mt-20 sm:mt-40 lg:mt-60">
          <span className="relative">
            <img
              src="images/icon 2.png"
              alt=" logo"
              className="h-16 sm:h-20 lg:h-24"
            />
          </span>
          <h1 className="text-white text-4xl sm:text-5xl lg:text-6xl font-bold mt-6 sm:mt-8 lg:mt-10">
            Access Innovative <br className="lg:hidden" />
            Healthcare
          </h1>
          <p className="text-white text-sm sm:text-base lg:text-lg mt-4 sm:mt-6 lg:mt-8">
            Empowering primary care professionals to seamlessly identify and
            engage with eligible patients for clinical trial opportunities.
          </p>
          <button className="bg-white text-[#392B85] mt-6 px-8 py-3 rounded-lg hover:bg-[#392B85] hover:text-white transition duration-300">
            Contact Us
          </button>
        </div>
      </div>
      <div className="container mx-auto mt-16 sm:mt-20 lg:mt-24 px-4 sm:px-6 lg:px-8 flex justify-center gap-6">
        <Card1 />
        <Card2 />
        <Card3 />
      </div>
    </div>
  );
};

export default Hero;
