import React, { useState } from "react";
import { HashLink as Link } from "react-router-hash-link";

const navLinks = [
  { to: "#", text: "Home" },
  { to: "#access", text: "About Us" },
  { to: "#solutions", text: "Solutions" },
  { to: "#images", text: "images/logo 1.png", isImage: true },
  { to: "#blogs", text: "Blogs" },
  { to: "#trails", text: "Trails" },
  { to: "#contact", text: "Contact Us" },
];

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div>
      <div className="flex gap-16 text-white justify-center items-center p-6 fixed bg-[#392B85] w-full z-20 cursor-pointer">
        {navLinks.map((link, index) => (
          <React.Fragment key={index}>
            {link.isImage && (
              <img
                src={link.text}
                alt="logo"
                className="h-8 w-auto hidden lg:block"
              />
            )}
            {!link.isImage && !isMenuOpen && (
              <span className="contrast-75 hidden lg:block">{link.text}</span>
            )}
          </React.Fragment>
        ))}
        <div className="lg:hidden flex-grow text-right">
          <button onClick={toggleMenu}>â˜°</button>
        </div>
        {isMenuOpen && (
          <div className="lg:hidden absolute top-16 right-6 bg-white p-2 rounded shadow">
            {navLinks.map((link, index) => (
              <Link
                key={index}
                to={link.to}
                smooth
                className="block py-2 hover:text-[#392B85]"
              >
                {link.text}
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Header;
