import React, { useState } from "react";

const Accordian1 = ({ isOpen, onClick }) => {
  // const [isOpen, setIsOpen] = useState(false);

  // const handleToggle = () => {
  //   setIsOpen(!isOpen);
  // };

  return (
    <div
      className={`w-[1264px] h-[90px] flex justify-between rounded-2xl mt-8 items-center border-2 border-gray-100 ${
        isOpen ? "bg-gray-100" : ""
      }`}
      onClick={onClick}
    >
      <div className="flex gap-4 ml-4">
        <img src="images/Group 87.png" alt="group" />
        <h1>1. Corriss Ambady</h1>
      </div>
      <div className="mr-4 cursor-pointer" onClick={onClick}>
        <img
          src={isOpen ? "images/Vector8.png" : "images/Vector9.png"}
          alt="vector"
        />
      </div>
      {isOpen && (
        <div className="absolute bg-gray-100 p-4 mt-2 font-light text-sm">
          Use our search feature, input your condition, and explore relevant
          trials matching your criteria.
        </div>
      )}
    </div>
  );
};

export default Accordian1;
