import React, { useState } from "react";
import { HashLink as Link } from "react-router-hash-link";

const navLinks = [
  { to: "#", text: "Home" },
  { to: "#access", text: "About Us" },
  { to: "#solutions", text: "Solutions" },
  { to: "#images", text: "images/logo 1.png", isImage: true },
  { to: "#blogs", text: "Blogs" },
  { to: "#trails", text: "Trails" },
  { to: "#contact", text: "Contact Us" },
];

const Header = () => {
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  const toggleMenu = () => {
    setIsSmallScreen(!isSmallScreen);
  };

  return (
    <div>
      <div
        className={`flex gap-16 text-white justify-${
          isSmallScreen ? "start" : "center"
        } items-center p-6 fixed bg-[#392B85] w-full z-20 cursor-pointer`}
      >
        <button onClick={toggleMenu} className="lg:hidden">
          â˜°
        </button>
        {isSmallScreen && (
          <div
            className={`fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 z-50 ${
              isSmallScreen ? "block" : "hidden"
            }`}
          ></div>
        )}
        {navLinks.map((link, index) => (
          <Link key={index} to={link.to} smooth>
            {link.isImage && !isSmallScreen ? (
              <img src={link.text} alt="logo" className="h-8 w-auto" />
            ) : (
              <span className="contrast-75">{link.text}</span>
            )}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Header;
