import React from "react";

const Article = () => {
  const containerStyle = {
    background: "linear-gradient(to right, #050F4E 50%, white 50%)",
    marginTop: "16px", // Adjust the top margin according to your requirements
    height: "642px", // Set the height of the container
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    position: "relative", // Set position to relative for proper positioning of absolute elements
  };

  const innerContainerStyle = {
    height: "470px", // Set the height of the inner container
    width: "1264px", // Set the width of the inner container
    background: "white",
    display: "flex",
    position: "relative", // Set position to relative for proper positioning of absolute elements
  };

  const textContainerStyle = {
    position: "absolute", // Position the text container absolutely within the inner container
    top: 0, // Align it to the top of the inner container
    left: 0, // Align it to the left of the inner container
    display: "flex",
    flexDirection: "column",
  };

  return (
    <div className="flex" style={containerStyle}>
      <div className="flex relative" style={innerContainerStyle}>
        <div style={textContainerStyle}>
          <h1 className="text-sm bg-[#ADB3BE] p-2">Article</h1>
          <h1 className="text-[#050F4E] text-4xl mt-4">
            The Benefits Of Digital Sequence <br /> Information In Biological
            Research.
          </h1>
        </div>
      </div>
    </div>
  );
};

export default Article;
