import React, { useState } from "react";
import { HashLink as Link } from "react-router-hash-link";
import { FaBars } from "react-icons/fa"; // Import the menu bar icon from react-icons/fa

const navLinks = [
  { to: "#", text: "Home" },
  { to: "#access", text: "About Us" },
  { to: "#solutions", text: "Solutions" },
  { to: "#images", text: "images/logo 1.png", isImage: true },
  { to: "#blogs", text: "Blogs" },
  { to: "#trails", text: "Trails" },
  { to: "#contact", text: "Contact Us" },
];

const Header = () => {
  const [dropdownVisible, setDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  return (
    <div>
      <div className="flex gap-16 text-white justify-center items-center p-6 fixed bg-[#392B85] w-full z-20 cursor-pointer">
        <FaBars className="lg:hidden cursor-pointer" onClick={toggleDropdown} />{" "}
        {/* Menu bar icon for small screens */}
        {navLinks.map((link, index) => (
          <Link key={index} to={link.to} smooth>
            {link.isImage ? (
              <img src={link.text} alt="logo" className="h-8 w-auto" />
            ) : (
              <span className="contrast-75">{link.text}</span>
            )}
          </Link>
        ))}
      </div>

      {/* Dropdown menu for small screens */}
      {dropdownVisible && (
        <div className="lg:hidden fixed top-0 left-0 w-full h-full bg-gray-900 bg-opacity-75 flex items-center justify-center">
          <div className="bg-white p-4 rounded-md">
            {navLinks.map((link, index) => (
              <Link
                key={index}
                to={link.to}
                smooth
                className="block text-[#392B85] text-center py-2 hover:bg-[#392B85] hover:text-white"
                onClick={toggleDropdown}
              >
                {link.text}
              </Link>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Header;
