import React, { useState } from "react";

const ExpencseDes = () => {
  const [activeSlide, setActiveSlide] = useState(0); // State to track active slide

  const handleDotClick = (index) => {
    setActiveSlide(index); // Update active slide when dot is clicked
  };

  const slides = [
    {
      imageUrl: "images/Rectangle1.png",
      text: "It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout.",
      name: "Coriss Ambady",
      designation: "Neurologist",
    },
    // Add more slide objects as needed
  ];

  return (
    <div className="flex justify-around">
      <div className="ml-16">
        <img src={slides[activeSlide].imageUrl} alt="rectangle" />
      </div>
      <div className="flex flex-col justify-center w-[620px] h-[470px] shadow-lg items-center text-center bg-white mr-20">
        <span>
          <img src="images/star.png" alt="star" />
        </span>
        <p className="mt-6">"{slides[activeSlide].text}"</p>
        <h1 className="text-[050F4E] font-bold mt-6">
          {slides[activeSlide].name}
        </h1>
        <p className="mt-1">{slides[activeSlide].designation}</p>
        <div className="mt-4 flex">
          {slides.map((slide, index) => (
            <img
              key={index}
              src="images/dot.png"
              alt="dot"
              className={`cursor-pointer mx-2 ${
                activeSlide === index ? "active" : ""
              }`}
              onClick={() => handleDotClick(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ExpencseDes;
