import React, { useState } from "react";
import { HashLink as Link } from "react-router-hash-link";
import { FiMenu } from "react-icons/fi"; // Import the menu bar icon from React Icons

const navLinks = [
  { to: "#", text: "Home" },
  { to: "#access", text: "About Us" },
  { to: "#solutions", text: "Solutions" },
  { to: "#images", text: "images/logo 1.png", isImage: true },
  { to: "#blogs", text: "Blogs" },
  { to: "#trails", text: "Trails" },
  { to: "#contact", text: "Contact Us" },
];

const Header = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const backgroundStyle = {
    backgroundImage: `url('images/Vector.png')`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <div>
      <div
        className="flex justify-center items-center p-6 fixed w-full z-20"
        style={backgroundStyle}
      >
        <div className="flex items-center">
          <button
            onClick={toggleDropdown}
            className="lg:hidden md:hidden sm:hidden text-white"
          >
            <FiMenu className="text-2xl" />
          </button>
          <div
            className={`hidden lg:flex md:flex sm:flex gap-16 text-white ${
              isDropdownOpen ? "hidden" : "flex justify-center"
            }`}
          >
            {navLinks.map((link, index) => (
              <Link key={index} to={link.to} smooth>
                {link.isImage ? (
                  <img src={link.text} alt="logo" className="h-8 w-auto" />
                ) : (
                  <span className="contrast-75">{link.text}</span>
                )}
              </Link>
            ))}
          </div>
        </div>
      </div>

      {isDropdownOpen && (
        <div className="lg:hidden md:hidden sm:hidden fixed inset-0 bg-[#392B85] z-30 flex justify-end items-center">
          <div className="flex flex-col gap-4">
            {navLinks.map((link, index) => (
              <Link
                key={index}
                to={link.to}
                smooth
                onClick={toggleDropdown}
                className="text-white hover:text-gray-300"
              >
                {link.isImage ? (
                  <img src={link.text} alt="logo" className="h-8 w-auto" />
                ) : (
                  <span>{link.text}</span>
                )}
              </Link>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Header;
